INSERT INTO public.quotes (text, author, source, category, aura_level) VALUES
  ('Tu n''as pas d''ennemis. Personne n''en a. Il n''y a personne qu''il soit acceptable de blesser.', 'Thors Snorresson', 'Vinland Saga', 'Stoïcisme', 100),
  ('Le bonheur de votre vie dépend de la qualité de vos pensées.', 'Marc Aurèle', 'Réalité', 'Mental', 95),
  ('Ne prie pas pour une vie facile, prie pour avoir la force d''en supporter une difficile.', 'Bruce Lee', 'Réalité', 'Discipline', 98),
  ('La peur n''est pas un mal. Elle t''apprend tes propres faiblesses. Une fois que tu les connais, tu deviens plus fort.', 'Gildarts Clive', 'Fairy Tail', 'Mental', 92),
  ('Le monde ne te donnera rien. Si tu veux quelque chose, tu dois le prendre.', 'Musashi Miyamoto', 'Vagabond', 'Ambition', 99),
  ('Qui va porter les bûches ? Je vais porter les bûches.', 'David Goggins', 'Réalité', 'Discipline', 100),
  ('Le destin est comme une roue de fer qui écrase tout sur son passage. Mais si tu ne bouges pas, elle t''écrasera de toute façon.', 'Guts', 'Berserk', 'Ambition', 99),
  ('Si vous n''êtes pas prêts à tout sacrifier pour gagner, vous ne méritez pas la victoire.', 'Itoshi Rin', 'Blue Lock', 'Ambition', 94),
  ('L''obstination est la clé. Si tu tombes sept fois, relève-toi huit.', 'Nana Shimura', 'My Hero Academia', 'Discipline', 91),
  ('Le travail acharné bat le talent quand le talent ne travaille pas dur.', 'Rock Lee', 'Naruto', 'Discipline', 96),
  ('On ne devient pas un champion dans le gymnase. On le devient par ce qu''on ressent : un désir, un rêve, une vision.', 'Muhammad Ali', 'Réalité', 'Ambition', 97),
  ('Je n''ai pas peur de l''homme qui a pratiqué 10 000 coups une fois, mais de celui qui a pratiqué un coup 10 000 fois.', 'Bruce Lee', 'Réalité', 'Discipline', 98),
  ('La force ne vient pas de la capacité physique, elle vient d''une volonté indomptable.', 'Mahatma Gandhi', 'Réalité', 'Mental', 93),
  ('Rien n''est impossible pour celui qui essaie.', 'Alexandre le Grand', 'Réalité', 'Ambition', 95),
  ('Lève-toi. Avance. Tu as tes jambes pour marcher, n''est-ce pas ?', 'Edward Elric', 'Fullmetal Alchemist', 'Discipline', 92),
  ('Si tu ne trouves pas de chemin, trace-le.', 'Elias', 'Réalité', 'Ambition', 90),
  ('Même si je dois mourir, je ne reculerai jamais devant mon destin.', 'Vegeta', 'Dragon Ball Z', 'Ambition', 99),
  ('Ce n''est pas parce que c''est difficile que nous n''osons pas, c''est parce que nous n''osons pas que c''est difficile.', 'Sénèque', 'Réalité', 'Stoïcisme', 96),
  ('Si tu veux monter, commence par descendre en toi-même.', 'Saint Augustin', 'Réalité', 'Mental', 91),
  ('Le plus grand risque est de ne prendre aucun risque.', 'Mark Zuckerberg', 'Réalité', 'Ambition', 90),
  ('Redeviens celui que tu étais avant que le monde ne te dise qui tu devais être.', 'Inconnu', 'Réalité', 'Mental', 93),
  ('Un homme qui ne peut pas se sacrifier ne peut rien changer.', 'Erwin Smith', 'L''Attaque des Titans', 'Ambition', 98),
  ('La douleur est temporaire. L''abandon est définitif.', 'Lance Armstrong', 'Réalité', 'Discipline', 95),
  ('Si tu ne peux pas voler, cours. Si tu ne peux pas courir, marche. Mais continue d''avancer.', 'Martin Luther King Jr.', 'Réalité', 'Discipline', 94),
  ('Je vais devenir le roi des pirates !', 'Monkey D. Luffy', 'One Piece', 'Ambition', 97),
  ('Ne meurs pas pour tes amis, vis for eux.', 'Erza Scarlet', 'Fairy Tail', 'Mental', 92),
  ('Le succès, c''est d''aller d''échec en échec sans perdre son enthousiasme.', 'Winston Churchill', 'Réalité', 'Ambition', 95),
  ('L''important n''est pas ce que l''on vous a fait, mais ce que vous faites de ce qu''on vous a fait.', 'Jean-Paul Sartre', 'Réalité', 'Mental', 93),
  ('Seul celui qui a touché le fond peut apprécier la lumière au sommet.', 'Sung Jin-Woo', 'Solo Leveling', 'Ambition', 98),
  ('Pour gagner, il faut être prêt à perdre.', 'Inconnu', 'Réalité', 'Ambition', 91);
